import React, {useState} from "react";
import "./App.scss";
// STEP 4 - import the button and display components
// Don't forget to import any extra css/scss files you build into the correct component
import Numbers from './ButtonComponents/NumberButtons/numbers';
import Operators from './ButtonComponents/OperatoButtons/Operator/operators';
import Specials from './ButtonComponents/SpecialButtons/Specials';
import Display from './DisplayComponents/display';
import Screen from './DisplayComponents/screen';

function App() {
  // STEP 5 - After you get the components displaying using the provided data file, write your state hooks here.
  // Once the state hooks are in place write some functions to hold data in state and update that data depending on what it needs to be doing
  // Your functions should accept a parameter of the the item data being displayed to the DOM (ie - should recieve 5 if the user clicks on
  // the "5" button, or the operator if they click one of those buttons) and then call your setter function to update state.
  // Don't forget to pass the functions (and any additional data needed) to the components as props
const [data, setData] = useState(0);
const [total, setTotal] = useState(0);
const changeData = (input) => {
  console.log(input);
  setData(input)
  setTotal(input)
}
  return (
    <div className="container">
        <Screen />
        {/* STEP 4 - Render your components here and be sure to properly import/export all files */}
        <Display data={total} />
      <div className="buttonHolder">
        <div>
          <Specials changeData = {changeData} />
          <Numbers changeData = {changeData} />
        </div>
        <div>
          <Operators changeData = {changeData}/>
        </div>
      </div>
    </div>
  );
}

export default App;
