import React from 'react'

export default function Counter(){
    const buttons = document.querySelectorAll('.counterBtn')
    let count= 0


    //Add event listeners and functionailty to each button  
    function change(){
        buttons.forEach((button) =>{
            if (button.classList.contains('down')){
            count--
            } else if (button.classList.contains('up')){
            count++
            }

            //Select the counter text
            const counter = document.querySelector('#counter')
            counter.textContent = count

            if (count < 0 ){
            counter.style.color = 'red'
            } else if (count > 0){
            counter.style.color = 'green'
            } else {
            counter.style.color = '#333333'
            }
        })
    }


    return(
        <div>
            <h1 id='counter'>{count}</h1>
            <button className='up' onClick={change}>up</button>
            <button className='down'onClick={change}>down</button>
        </div>
    )
}
